mapper.select:
SELECT SERIAL_NO AS serialNumber
FROM TBR_IOT_DEVICE
WHERE DEVC_ID = #{deviceId}

SELECT
    *
FROM
    TBR_OPR_DEVICE_DETAIL
WHERE
    TEMP_DEVC_REG_KEY = #{tmpRegistKey}
AND
    DEVC_ID = #{deviceId}
AND
    HP = #{hp}
AND
    USER_ID = #{userId}

mapper.update:
UPDATE
    TBR_OPR_DEVICE_DETAIL
    SET DEVC_NICK = #{newDeviceNickname}
WHERE
    DEVC_ID = #{deviceId}

UPDATE
    TBT_OPR_DEVICE_REGIST
SET
    LAT = #{latitude},
    LON = #{longitude}
WHERE
    TEMP_DEVC_REG_KEY = #{tmpRegistKey}
AND
    DEVC_ID = #{deviceId}
