mapper.update:
UPDATE
    TBR_OPR_ACCOUNT
SET
    PW = #{newPassword}
WHERE
    USER_ID = #{userId}

mapper.select:
SELECT PW AS userPassword
FROM TBR_OPR_ACCOUNT
WHERE USER_ID = #{userId};

mapper.update:
UPDATE
    TBD_IOT_GRP_INFO
SET
    USER_NICK = #{userNickname}
WHERE
    USER_ID = #{userId}

UPDATE TBR_OPR_USER_DEVICE_PUSH
PUSH_YN_01 = #{fPushYn}, PUSH_YN_02 = #{sPushYn}, PUSH_YN_03 = #{tPushYn},
WHERE USER_ID = #{userId} AND DEVC_ID = #{deviceId}

UPDATE TBR_OPR_DEVICE_DETAIL
SET ADDR_NICK = #{newDeviceLocNickname}
WHERE DEVC_ID = #{deviceId}

UPDATE
    TBT_OPR_DEVICE_REGIST
SET
    ADDR_NICK = #{newDeviceLocNickname}
WHERE
    DEVC_ID = #{deviceId}

UPDATE
    TBR_OPR_ACCOUNT
SET
    PUSH_TOKEN = #{pushToken}
WHERE
    USER_ID = #{userId}

mapper.select:
SELECT PUSH_YN_01 AS fPushYn, PUSH_YN_02 AS sPushYn, PUSH_YN_03 AS tPushYn FROM TBR_OPR_USER_DEVICE_PUSH WHERE USER_ID IN
#{item.userId}
AND DEVC_ID IN
#{item.deviceId}

mapper.update:
UPDATE
    TBD_IOT_GRP_INFO
SET
    GRP_NM = #{userId} 
WHERE
    GRP_NM = #{groupName}

UPDATE
    TBR_IOT_DEVICE_GRP_INFO
SET
    GRP_ID = #{userId}
WHERE
    GRP_ID = #{groupName}

UPDATE
    TBD_USER_INVITE_GROUP
SET
    GRP_IDX = #{idx}
WHERE
    idx = #{idx}
